import React from 'react';
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink, MDBContainer, MDBMask, MDBView } from 'mdbreact';
import { BrowserRouter as Router } from 'react-router-dom';

class FullPageIntroWithFixedNavbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,
      isWideEnough: false
    };
    this.onClick = this.onClick.bind(this);
    this.callbisection= this.callbisection.bind(this);
    this.drawChart= this.drawChart.bind(this);
    this.Bisection= this.Bisection.bind(this);
    this.culateFunctionion= this.calculateFunction.bind(this);calculateFunction

  }

  onClick() {
    this.setState({
      collapse: !this.state.collapse
    });
  }
   callbisection() {
    var xl = parseFloat(document.getElementById("xlInput").value);
    var xr = parseFloat(document.getElementById("xrInput").value);
    var rowcount = document.getElementById("myTable").getElementsByTagName("tr").length;
    var xm = (xl + xr) / 2;
    console.log(xm);
    var graph = new Array();
    var x = new Array();
    var i;
    var j;
    for (i = parseInt(xl); i <= parseInt(xr); i += 0.001) {
      x.push(i);
      graph.push(calculateFunction(i));
    }
    try {
      for (j = 1; j < rowcount; j++) {
        document.getElementById("myTable").deleteRow(1);
      }
    } catch {
      console.log('rowcount:', rowcount);
    }
    drawChart(graph, x); //เขียนกราฟ
    document.getElementById("out").innerHTML = Bisection(xl, xr, xm, xl, 0)
    let pm1 = new Promise((resolve, reject) => {
      setTimeout(function() {
        resolve('timeout');
      }, 4000);
    });
    pm1.then(() => {
      $("#btn1").addClass("btn btn-outline-primary").removeClass("btn-success");
      $("#btn1").text("Calculate");
      //console.log("Promise Success");
    }).catch((message) => {
      console.log(message);
    });
  }

   Bisection(xl, xr, xm, old, i) {
    var fxl = calculateFunction(xl);
    var fxr = calculateFunction(xr);
    var fxm = calculateFunction(xm);
    var err;
    try {
      err = Math.abs((xm - old) / xm);
    } catch {
      err = 1000;
    }
    console.log("Iteration:", i);
    console.log('error: ', err);
    console.log('xl:', xl);
    console.log('fxl:', fxl);
    console.log('xr:', xr);
    console.log('fxr:', fxr);
    console.log('xm:', xm);
    console.log('fxm:', fxm);
    var table = document.getElementById("myTable");
    // Create an empty <tr> element and add it to the 1st position of the table:
    var row = table.insertRow(i + 1);
    // Insert new cells (<td> elements) at the 1st and 2nd position of the "new" <tr> element:
    var cell1 = row.insertCell(0);
    var cell2 = row.insertCell(1);
    var cell3 = row.insertCell(2);
    var cell4 = row.insertCell(3);
    var cell5 = row.insertCell(4);
    // Add some text to the new cells:
    cell1.innerHTML = i;
    cell2.innerHTML = xl;
    cell3.innerHTML = xr;
    cell4.innerHTML = xm;
    cell5.innerHTML = err;
    if (err <= 0.0000001 || i > 100) {
      return xm;
    }
    if (fxl < fxr) {
      if (fxm > 0) {
        return Bisection(xl, xm, (xl + xm) / 2, xm, i + 1);
      } else {
        return Bisection(xm, xr, (xr + xm) / 2, xm, i + 1);
      }
    } else {
      if (fxm > 0) {
        return Bisection(xm, xr, (xr + xm) / 2, xm, i + 1);
      } else {
        return Bisection(xl, xm, (xl + xm) / 2, xm, i + 1);
      }
    }
  }

  calculateFunction(inputx) {
    var str = document.getElementById("txtbox1").value;
    const ans = math.compile(str);
    let scope = {
      x: parseFloat(inputx)
    };
    return ans.eval(scope)
  }

  drawChart(data, x) //เขียนกราฟ
  {
    var trace1 = {
      x: x,
      y: data,
      name: 'Y',
      type: 'scatter'
    };
    var data = [trace1];
    var layout = {
      title: 'Graph Of F(x)',
      yaxis: {
        title: 'Y'
      },
      yaxis2: {
        title: 'yaxis2 title',
        titlefont: {
          color: 'rgb(148, 103, 189)'
        },
        tickfont: {
          color: 'rgb(148, 103, 189)'
        },
        overlaying: 'y',
        side: 'right'
      },
      xaxis: {
        title: 'X'
      }
    };
    Plotly.newPlot('mychart', data, layout, {
      showSendToCloud: true
    });
  }
  render() {
    return (
      <div>
        <header>
          <Router>
            <MDBNavbar color="indigo" dark expand="md" fixed="top">
              <MDBNavbarBrand href="/">
                <strong>Numerical Method</strong>
              </MDBNavbarBrand>
              {!this.state.isWideEnough && <MDBNavbarToggler onClick={this.onClick} />}
              <MDBCollapse isOpen={this.state.collapse} navbar>
                <MDBNavbarNav left>
                  <MDBNavItem active>
                    <MDBNavLink to="#">Chapter 1</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink to="#">Chapter 2</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink to="#">Chapter 3</MDBNavLink>
                  </MDBNavItem>
                </MDBNavbarNav>
              </MDBCollapse>
            </MDBNavbar>
          </Router>

          <MDBView src="https://mdbootstrap.com/img/Photos/Others/img%20(50).jpg">
            <MDBMask overlay="black-light" className="flex-center flex-column text-white text-center">
              <h1>Numerical Method</h1>
              <br />
              <p></p>
            </MDBMask>
          </MDBView>
        </header>
        <br /><br />
        <div class="row" >
      <div class="col-lg-8 col-md-10 mx-auto">
        <div name="calc">
          <div class="input-group-sm mb-3">
            <input type="text" id="txtbox1" class="form-control" placeholder="Enter Function " aria-label="Username" aria-describedby="basic-addon1" /><br />
            <input type="text" id="xlInput" class="form-control" placeholder="Enter XL Value" aria-label="Username" aria-describedby="basic-addon1" /><br />
            <input type="text" id="xrInput" class="form-control" placeholder="Enter XR Value" aria-label="Username" aria-describedby="basic-addon1" /><br />
          </div>
          <center >
            <button onclick="callbisection()" id="btn1" class="btn btn-outline-secondary">Calculate</button>
            <p id="out" class="">Answer</p>
            <div id="mychart" style={{height:'480px', width:"640px" }}>
            </div>
            <button class="btn btn-secondary" data-toggle="collapse" href="#multiCollapseExample1" role="button" aria-expanded="false" aria-controls="multiCollapseExample1" style={{marginBottom: "20px"}}>Display Table</button>
          </center>
          <div class="collapse multi-collapse" id="multiCollapseExample1">
            <center>
              <table class="table table-dark" id="myTable">
                <thead>
                  <tr>
                    <th scope="col">Iteration</th>
                    <th scope="col">XL</th>
                    <th scope="col">XR</th>
                    <th scope="col">XM</th>
                    <th scope="col">Error</th>
                  </tr>
